import React, {Component} from 'react';
import './BarChart.css';
import 'c3/c3.css';
import c3 from 'c3';

class BarChart extends Component{

    constructor(props){
        super(props);
        this.renderChart = this.renderChart.bind(this);
    }

    renderChart(){

        c3.generate({
            bindto: "#bar",
            data: {
                columns: this.props.series,type : 'bar'
            },
            bar: {
                title: "Daily",
                width:{
                    ratio: 0.3
                }
            },
            transition: {
                duration: 750
            }
        });


    }
    componentDidMount(){
        this.renderChart();
    }
     componentDidUpdate(){
         this.renderChart();
     }

    render(){


        return(
            <div className="BarChartBox">
                <div className="Bar" id="bar"></div>

            </div>
        );
    }

}

export default BarChart;
