import React, {Component} from 'react';
import './InfoBox.css';

class InfoBox extends Component{


    render(){

        const pStyle={
            fontSize: '30px',
            fontWeight: 'bold',
            color: '#b99362' 
        }

        const h2Style = {
            borderBottom: 'solid 1px #333'
        }
        return(
            <div className="Info">
                <h2 stylr={h2Style}> Reporting Cases of COVID-19 </h2>
                <p class="nowstate" style={pStyle}>{this.props.nowState}</p>
            </div>
        );
    }

}

export default InfoBox;
