import React,{Component} from 'react';
import Map from './Component/Map';
import InfoBox from './Component/InfoBox';
import LineChart from './Component/LineChart';
import './App.css';


class App extends Component {

  state = {
    beginString:'Hi, this is a visualization map for showing COVID-19 confirmed cases in United States. Now you can click on the map to see trend chart for each states. The open data is on the Github provided by JHU CSSE.',
    NowState:'',
    response:'',
    NowSeries:[],
    showChart: false,
    showDynamicMap: false,
    colors:['#61c661','#b1dbb9','#ffd950','#f2b691','#f49a61','#f4833d','#f76d17','ff5619'],
    stateColor:{}
  }


  generateColor=()=>{
    for(var key in this.state.response.states){
      const series = this.state.response.states[key].map(num=>{
        if(num<=10){
          return this.state.colors[0];
        }
        else if(num>10 && num<=1000){
          return this.state.colors[1];
        }
        else if(num>1000 && num<=5000){
          return this.state.colors[2];
        }
        else if(num>5000 && num<=10000){
          return this.state.colors[3];
        }
        else if(num>10000 && num<=50000){
          return this.state.colors[4];
        }
        else if(num>50000 && num<=100000){
          return this.state.colors[5];
        }
        else if(num>100000){
          return this.state.colors[6];
        }

      })


    }

  }

  componentDidMount(){

    this.callApi()
      .then(res => this.setState({ response: res })) //
      .catch(err => console.log(err));
  }
  callApi = async () => {
    const response = await fetch('/api'); 
    console.log(response);
    const body = await response.json();
    console.log("success");
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  onClick=(e)=>{
    this.setState({showChart:true});
    let Nowstate = e.target.getAttribute('data-name');
    this.setState({NowState:Nowstate});

    if(this.state.response.states.hasOwnProperty(String(Nowstate))){
       this.setState({NowSeries:this.state.response.states[Nowstate]});
     }

    //console.log(this.state.response.states[Nowstate]);
  }

  render() {

    return(
      <div className="App">
        <Map onHover={this.onClick}/>
        <div className="InfoBox">
          <InfoBox nowState={this.state.NowState}/>
          {this.state.showChart? null:
          <p className="floating">{this.state.beginString}</p>
          }
          {this.state.showChart?
          <LineChart series={this.state.NowSeries} date={this.state.response.date}/>: null
          }

        </div>
      </div>
    );
  }
}



export default App;
