import React,{Component} from 'react';
import Map from './Component/Map';
import InfoBox from './Component/InfoBox';
import LineChart from './Component/LineChart';
import './App.css';


class App extends Component {

  state = {
    beginString:'Hi, this is a visualization map for showing COVID-19 confirmed cases in United States. Now you can click on the map to see trend chart for each states. The open data is on the Github provided by JHU CSSE.',
    NowState:'',
    response:'',
    NowSeries:[],
    showChart: false,
    showDynamicMap: false,
    colors:['#61c661','#b1dbb9','#c6e9c6','#ffe693','#ffd950','#ffbb56','#f2b691','#f49a61','#f4833d','#f76d17','ff5619','#ff3f00'],
    stateColor: {'Alabama':[], 'Alaska':[], 'Arizona':[],'Arkansas':[], 'California':[], 'Colorado':[], 'Connecticut':[], 'Delaware':[],'District of Columbia':[], 'Florida':[], 'Georgia':[], 'Hawaii':[], 'Idaho':[],
    'Illinois':[], 'Indiana':[], 'Iowa':[], 'Kansas':[], 'Kentucky':[], 'Louisiana':[],'Maine':[], 'Maryland':[], 'Massachusetts':[], 'Michigan':[], 'Minnesota':[],
    'Mississippi':[], 'Missouri':[], 'Montana':[], 'Nebraska':[], 'Nevada':[],'New Hampshire':[], 'New Jersey':[], 'New Mexico':[], 'New York':[],
    'North Carolina':[], 'North Dakota':[], 'Ohio':[], 'Oklahoma':[], 'Oregon':[],'Pennsylvania':[], 'Rhode Island':[], 'South Carolina':[], 'South Dakota':[],
    'Tennessee':[], 'Texas':[], 'Utah':[], 'Vermont':[], 'Virginia':[], 'Washington':[],'West Virginia':[], 'Wisconsin':[], 'Wyoming':[],'Grand Princess':[]}
    }


  generateColor=()=>{
    // let state_color = [];
    //let copy = this.state.response.states;
    //this.setState({stateColor:copy});
    //console.log(this.state.stateColor);
    for(let key in this.state.response.states){

      let series = this.state.response.states[key].map((num)=>{
        if(num<=10){
          return this.state.colors[0];
        }
        else if(num>10 && num<=100){
          return this.state.colors[1];
        }
        else if(num>100 && num<=500){
          return this.state.colors[2];
        }
        else if(num>500 && num<=1000){
          return this.state.colors[3];
        }
        else if(num>1000 && num<=2000){
          return this.state.colors[4];
        }
        else if(num>2000 && num<=4000){
          return this.state.colors[5];
        }
        else if(num>4000 && num<=8000){
          return this.state.colors[6];
        }
        else if(num>8000 && num<=12000){
          return this.state.colors[7];
        }
        else if(num>12000 && num<=16000){
          return this.state.colors[8];
        }
        else if(num>16000 && num<=20000){
          return this.state.colors[9];
        }
        else if(num>20000 && num<=30000){
          return this.state.colors[10];
        }
        else if(num>30000){
          return this.state.colors[11];
        }

      })

      if(this.state.stateColor.hasOwnProperty(key)){   
        const newColor  = this.state.stateColor;
        newColor[key] = series;
        this.setState({stateColor:newColor});
      }
      else{
        this.setState({stateColor:{...this.state.stateColor, [key]:series}});
      }
    }



  }

  componentDidMount(){

    this.callApi()
      .then(res => this.setState({ response: res })) //
      .catch(err => console.log(err));



  }
  callApi = async () => {
    const response = await fetch('/api'); 
    console.log(response);
    const body = await response.json();
    console.log("success");
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  onClick=(e)=>{
    this.generateColor();
    this.setState({showChart:true,showDynamicMap: false});
    let Nowstate = e.target.getAttribute('data-name');
    this.setState({NowState:Nowstate});

    if(this.state.response.states.hasOwnProperty(String(Nowstate))){
       this.setState({NowSeries:this.state.response.states[Nowstate]});
     }

    //console.log(this.state.response.states[Nowstate]);
  }

  onClickShowBtn=()=>{
    this.setState({showChart: false,showDynamicMap: true,NowState:'The United State'});
  }

  render() {

    return(
      <div className="App">
        <div className="Cockpit">
          <Map className="map" onHover={this.onClick} data={this.state.stateColor}/> 
          {this.state.showDynamicMap?<div className="scale"></div>:null}
          {this.state.showDynamicMap?<div className="showDyBtn" onClick={this.onClickShowBtn}> Time Series</div>:null}
        </div>
        <div className="InfoBox">
          <InfoBox nowState={this.state.NowState}/>
          {this.state.showChart? null:
          <p className="floating">{this.state.beginString}</p>
          }
          {this.state.showChart?
          <LineChart series={this.state.NowSeries} date={this.state.response.date}/>: null
          }

        </div>
      </div>
    );
  }
}



export default App;
