import React,{Component} from 'react';
import Map from './Component/Map';
import InfoBox from './Component/InfoBox';
import './App.css';


class App extends Component {

  state = {
    NowState:''
  }

  componentDidMount(){
    fetch("https://github.com/CSSEGISandData/COVID-19/blob/master/csse_covid_19_data/csse_covid_19_time_series/time_series_covid19_confirmed_US.csv/get")
      .then(res => res.json())
      .then((result) => {

        console.log(result);
