import React, {Component} from 'react';
import './LineChart.css';
import 'c3/c3.css';
import c3 from 'c3';

class LineChart extends Component{

    constructor(props){
        super(props);
        this.renderChart = this.renderChart.bind(this);
    }

    renderChart(){
        const data = this.props.series;
        let time_series = ['time series']; 
        time_series = time_series.concat(data); 

        //console.log(this.props);
        const date = this.props.date;
        let date_series = ['x']; 
        date_series = date_series.concat(date); 

        let myData = {};
        myData.x = 'x';
	    myData.xFormat = "%Y-%m-%d";
	    myData.type = 'bar';
	    let myX = date;
	    let myY = data;
	    myX.splice(0, 0, 'x');
	    myY.splice(0, 0, 'New Reports');
	    myData.columns = [];
	    myData.columns.push(myX);
	    myData.columns.push(myY);

        c3.generate({
            bindto: '#line',
            data: myData,
            size: {
              height: 480,
              width: 400,
            },
            bar: {
              width: {
                ratio: 0.5 // this makes bar width 50% of length between ticks
              }
              // or
              //width: 100 // this makes bar width 100px
            },
            axis: {
              x: {
                type: 'timeseries',
              tick: {
                      format: "%m/%d/%Y"
                  }
              },

            }
          })


    }
    componentDidMount(){
        //this.renderChart();
    }
    componentDidUpdate(){
        this.renderChart();
    }

    render(){


        return(
            <div className="Line" id="line"></div>
        );
    }

}

export default LineChart;
