import React, {Component} from 'react';
import './LineChart.css';
import 'c3/c3.css';
import c3 from 'c3';

class LineChart extends Component{

    constructor(props){
        super(props);
        this.renderChart = this.renderChart.bind(this);
    }

    renderChart(){
        const data = this.props.series;
        let time_series = ['time series']; 
        time_series = time_series.concat(data); 

        const date = this.props.series;
        let date_series = ['date series']; 
        date_series = date_series.concat(date); 




        c3.generate({
            bindto: "#line",
            data: {
                x: 'time series',
        //        xFormat: '%Y%m%d', // 'xFormat' can be used as custom format of 'x'
                columns: [
                    date_series,
                    time_series
                ]
            },
            axis: {
                x: {
                    type: 'timeseries',
                    // tick: {
                    //     format: '%d/%m/%Y'
                    // }
                }
            }
        });


    }
    componentDidMount(){
        this.renderChart();
    }
     componentDidUpdate(){
         this.renderChart();
     }

    render(){


        return(
            <div className="Line" id="line"></div>

        );
    }

}

export default LineChart;
