import React, {Component} from 'react';
import './BarChart.css';
import 'c3/c3.css';
import c3 from 'c3';

class BarChart extends Component{

    constructor(props){
        super(props);
        this.renderChart = this.renderChart.bind(this);
    }

    renderChart(){

        c3.generate({
            bindto: "#bar",
            data: {
                columns: this.props.series,type : 'bar'
            },
            bar: {
                title: "Daily",
                width:{
                    ratio: 0.3
                }
            },
            transition: {
                duration: 750
            }
        });


    }
    componentDidMount(){
        this.renderChart();
    }
     componentDidUpdate(){
         this.renderChart();
     }

    render(){

        const data = {};
        // console.log(this.props.data);
        let len = this.props.data.length;
        // console.log(this.props.data[0].date);
        for(var i=0; i<len;i++) {
            if(data.hasOwnProperty(this.props.data[i].date)){
                data[String(this.props.data[i].date)] += Number(this.props.data[i].money);
            }
            else{
                data[String(this.props.data[i].date)] =  Number(this.props.data[i].money);
            }
        }
        var result = Object.keys(data).map(function (key) { 
            return [key, data[key]]; 
        });


         let Descript = null;
         Descript = ( 
            result.map((item,index) =>{
                 return (
                     <div className="ItemName"  key={index}>
                         <div >{item[0]} :</div>
                         <div>{item[1]} $</div>
                     </div>
                 )
             })
          );

        return(
            <div className="BarChartBox">
                <div className="Bar" id="bar"></div>
                <div className="Descript">
                    <div className="DesItem"> 
                        {Descript}
                    </div>
                </div>
            </div>
        );
    }

}

export default BarChart;
